<?php    use DiDom\Document;    use Lib\MyLib;    set_time_limit(0);    require __DIR__ . '/../vendor/autoload.php';    // run    function crawlData()    {        $url = "https://eva.vn/tin-tuc/cong-bo-ca-61-mac-covid-19-tai-viet-nam-c73a425341.html";        if (MyLib::get_data($url, $content)) {            $dom = new Document();            $dom->load($content);            $content = $dom->find('#baiviet-container')[0];                $trs = $content->find('.ant-table-row.ant-table-row-level-0');                $result = [];                foreach ($trs as $tr) {                    $td = $tr->find('td');                    $result[] = [                        "quocgia" => $td[0]->text(),                        "socanhiem" => $td[1]->text(),                        "tuvong" => $td[2]->text(),                        "binhphuc" => $td[3]->text()                    ];                }               return $result;        }    }    function crawlData_world()    {        $url = "https://www.worldometers.info/coronavirus/";        if (MyLib::get_data($url, $content)) {            $dom = new Document();            $dom->load($content);            $content = $dom->find('#main_table_countries_div')[0];              echo $content;        }    }?><!doctype html><html lang="en"><head>    <meta charset="UTF-8">    <meta name="viewport"          content="width=device-width, user-scalable=no, initial-scale=1.0, maximum-scale=1.0, minimum-scale=1.0">    <meta http-equiv="X-UA-Compatible" content="ie=edge">    <title>Corona</title></head><body>    <table>        <tr>            <td>Quốc gia</td>            <td>Số ca nhiễm</td>            <td>Số ca tử vong</td>            <td>Chữa khỏi</td>        </tr>    <?php        $result = crawlData();        foreach ($result as $r){     ?>    <tr>        <td> <?php echo $r['quocgia']; ?> </td>        <td> <?php echo $r['socanhiem']; ?> </td>        <td> <?php echo $r['tuvong']; ?> </td>        <td> <?php echo $r['binhphuc']; ?> </td>    </tr>    <?php    }    ?>    </table></body></html>